import 'package:flutter/material.dart';

void main() {
  runApp(MyApp());
}

class MyApp extends StatelessWidget {
  @override
  Widget build(BuildContext context) {
    return MaterialApp(
      title: 'Flutter Navigation Example:هدى محمد عكران',
      home: HomePage(),
    );
  }
}

class HomePage extends StatefulWidget {
  @override
  _HomePageState createState() => _HomePageState();
}

class _HomePageState extends State<HomePage> {
  int _currentIndex = 0;

  final List<Widget> _children = [
    Container(
      color: Colors.red,
      child: Center(
        child: Image.network('data:image/jpeg;base64,'),
      ),
    ),
    Container(
      color: Colors.green,
      child: Center(
        child: Image.network('data:image/jpeg;base64,'),
      ),
    ),
    Container(
      color: Colors.blue,
      child: Center(
        child: Image.network('data:image/jpeg;base64,'),
      ),
    ),
  ];

  void onTabTapped(int index) {
    setState(() {
      _currentIndex = index;
    });
  }

  @override
  Widget build(BuildContext context) {
    return Scaffold(
      appBar: AppBar(
        title: Text('Flutter Navigation Example:هدى عكران'),
      ),
      body: _children[_currentIndex],
      bottomNavigationBar: BottomNavigationBar(
        currentIndex: _currentIndex,
        onTap: onTabTapped,
        items: [
          BottomNavigationBarItem(
            icon: Icon(Icons.image),
            label: 'Container 1',
          ),
          BottomNavigationBarItem(
            icon: Icon(Icons.image),
            label: 'Container 2',
          ),
          BottomNavigationBarItem(
            icon: Icon(Icons.image),
            label: 'Container 3',
          ),
        ],
      ),
    );
  }}
